import './icons.js'
import Swiper from './Swiper'

console.log('Hello ,world')


const $ = selector => document.querySelector(selector)
const $$ = selector => document.querySelectorAll(selector)

class Player {
  constructor(node) {
    this.root = typeof node === 'string' ? $(node) : node
    this.songList = []
    this.currentIndex = 0
    this.audio = new Audio()
    this.lyricsArr = []
    this.lyricIndex = -1
    this.start()
    this.bind()
  }
  start() {
    fetch('https://jirengu.github.io/data-mock/huawei-music/music-list.json')
      .then(res => res.json())
      .then(data => {
        // console.log(data)
        this.songList = data
        this.renderSong()

      })
  }


  bind() {
    let self = this
    this.root.querySelector('.btn-play-pause').onclick = function () {


      if (this.classList.contains('playing')) {
        this.querySelector('use').setAttribute('xlink:href', '#icon-play')
        self.audio.pause()
        this.classList.remove('playing')
        this.classList.add('pause')
      } else if (this.classList.contains('pause')) {
        this.querySelector('use').setAttribute('xlink:href', '#icon-pause')
        self.audio.play()
        this.classList.remove('pause')
        this.classList.add('playing')

      }

    }
    this.root.querySelector('.btn-pre').onclick = function () {
      self.playPrevSong()
      self.renderSong()


    }
    this.root.querySelector('.btn-next').onclick = function () {
      self.playNextSong()
      self.renderSong()


    }
    let swiper = new Swiper(this.root.querySelector('.panels'))
    swiper.on('swipLeft', function () {
      this.classList.remove('panel1')
      this.classList.add('panel2')
      console.log('left')
    })
    swiper.on('swipRight', function () {
      this.classList.remove('panel2')
      this.classList.add('panel1')
      console.log('right')
    })
    this.audio.ontimeupdate = function () {
      // console.log(parseInt(self.audio.currentTime * 1000))
      self.locateLyric()
      self.setProgerssBar()
    }

  }
  pauseSong() {
    this.audio.pause()
  }

  renderSong() {
    let songObj = this.songList[this.currentIndex]
    // console.log(this.root.querySelector('.header h1'))
    this.root.querySelector('.header h1').innerText = songObj.title
    this.root.querySelector('.header p').innerText = songObj.author + '-' + songObj.albumn
    this.audio.src = songObj.url
    this.audio.onloadedmetadata = () => this.root.querySelector('.time-end').innerText = this.formateTime(this.audio.duration)
    this.loadLyrics()
  }
  palySong() {

    this.audio.play()
  }
  locateLyric() {
    console.log('locateLyric')
    let currentTime = this.audio.currentTime * 1000
    console.log(currentTime)
    let nextLineTime = this.lyricsArr[this.lyricIndex + 1][0]
    console.log(nextLineTime)
    if (currentTime > nextLineTime && this.lyricIndex < this.lyricsArr.length - 1) {
      this.lyricIndex++
      let node = this.root.querySelector('[data-time="' + this.lyricsArr[this.lyricIndex][0] + '"]')
      console.log(node)
      if (node) this.setLineToCenter(node)
      this.root.querySelectorAll('.panel-effect .lyric p')[0].innerText = this.lyricsArr[this.lyricIndex][1]
      this.root.querySelectorAll('.panel-effect .lyric p')[1].innerText = this.lyricsArr[this.lyricIndex + 1] ? this.lyricsArr[this.lyricIndex + 1][1] : ''
    }
  }
  playPrevSong() {
    this.currentIndex = (this.songList.length + this.currentIndex - 1) % this.songList.length
    this.audio.src = this.songList[this.currentIndex].url
    this.audio.oncanplaythrough = () => this.audio.play()
  }
  playNextSong() {
    this.currentIndex = (this.songList.length + this.currentIndex + 1) % this.songList.length
    this.renderSong()
    this.audio.src = this.songList[this.currentIndex].url
    this.audio.oncanplaythrough = () => this.audio.play()
  }
  loadLyrics() {
    fetch(this.songList[this.currentIndex].lyric)
      .then(res => res.json())
      .then(data => {
        this.setLyrics(data.lrc.lyric)
        window.lyric = data.lrc.lyric
      })
  }
  setLineToCenter(node) {
    console.log(node)
    let offset = node.offsetTop - this.root.querySelector('.panels').offsetHeight / 2
    offset = offset > 0 ? offset : 0
    console.log('11111111')
    console.log(offset)
    this.root.querySelector('.panel-lyrics .container').style.transform = `translateY(-${offset}px)`

    this.root.querySelectorAll('.panels .container p').forEach(node => { node.classList.remove('current') })
    node.classList.add('current')
  }
  setProgerssBar() {

    let percent = (this.audio.currentTime * 100 / this.audio.duration) + '%'
    // console.log(percent)

    this.root.querySelector('.bar .progress').style.width = percent
    this.root.querySelector('.time-start').innerText = this.formateTime(this.audio.currentTime)
  }
  formateTime(secondsTotal) {
    let minutes = parseInt(secondsTotal / 60)
    minutes = minutes >= 10 ? "+ minutes" : '0' + minutes
    let seconds = parseInt(secondsTotal % 60)
    seconds = seconds >= 10 ? '' + seconds : '0' + seconds
    return minutes + ':' + seconds
  }
  setLyrics(lyrics) {
    this.lyricIndex = 0
    let fragment = document.createDocumentFragment()
    let lyricsArr = []
    this.lyricsArr = lyricsArr
    lyrics.split(/\n/).filter(str => str.match(/\[.+?\]/))
      .forEach(line => {
        let str = line.replace(/\[.+?\]/g, '')
        line.match(/\[.+?\]/g).forEach(t => {
          t = t.replace(/[\[\]]/g, '')
          let milliseconds = parseInt(t.slice(0, 2)) * 60 * 1000 + parseInt(t.slice(3, 5)) * 1000 + parseInt(t.slice(6))
          lyricsArr.push([milliseconds, str])
        })
      })
    lyricsArr.filter(line => line[1].trim() !== '').sort((v1, v2) => {
      if (v1[0] > v2[0]) {
        return 1
      } else {
        return -1
      }
    }).forEach(line => {
      let node = document.createElement('p')
      node.setAttribute('data-time', line[0])
      node.innerText = line[1]
      fragment.appendChild(node)
    })
    this.root.querySelector('.panel-lyrics .container').innerHTML = ''
    this.root.querySelector('.panel-lyrics .container').appendChild(fragment)


  }
}


window.p = new Player('#player')
